(()=>{let e=document.querySelector(".city-input"),t=document.querySelector(".submit-btn"),n=document.querySelector(".search-text"),r=document.querySelector(".temp-result"),o=document.querySelector(".pressure-result"),u=document.querySelector(".humidity-result"),c=document.querySelector(".precipitation-result"),a=document.querySelector(".wind-result"),i=document.querySelector(".main-weather");t.addEventListener("click",(t=>{let l=e.value;n.textContent=l,t.preventDefault(),async function(){let e=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${l}&appid=ENV[API_TOKEN]`);return e=await e.json(),i.textContent=`${e.weather[0].description}`,r.textContent=`${Math.floor(e.main.temp-273)}C`,o.textContent=`${e.main.pressure}mm`,u.textContent=`${e.main.humidity}%`,c.textContent=`${e.weather[0].main}`,a.textContent=e.wind.speed,e}().catch((e=>{console.error(e)}))}))})();