(()=>{"use strict";const t=async()=>{const t=document.querySelector(".pressure-result"),e=document.querySelector(".humidity-result"),n=document.querySelector(".precipitation-result"),o=document.querySelector(".wind-result"),r=document.querySelector(".time-result"),u=document.querySelector(".main-weather"),a=document.querySelector(".loader"),c=document.querySelector(".city-input"),i=document.querySelector(".search-text"),s=document.querySelector(".temp-result"),m=document.querySelector(".temp-unit");let C=0;const l=c.value,d=/[a-zA-Z]/;try{if(l.match(d)){a.classList.remove("hide");let c=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${l}&appid=f1626be87a0ae483be20b659210c16a3`);return c=await c.json(),a.classList.add("hide"),i.textContent=l.toUpperCase(),u.textContent=`${c.weather[0].description}`,C=Math.floor(c.main.temp-273),s.textContent=`${C}°C`,t.textContent=`${c.main.pressure}mm`,e.textContent=`${c.main.humidity}%`,n.textContent=`${c.weather[0].main}`,o.textContent=`${c.wind.speed}m/s`,r.textContent=c.timezone<0?"UTC"+c.timezone/3600:"UTC+"+c.timezone/3600,h=s,p=C,(x=m).addEventListener("click",(t=>{t.preventDefault(),"Celsius"===x.textContent?(x.textContent="Fahrenheit",h.textContent=`${p}°C`):"Fahrenheit"===x.textContent?(x.textContent="Celsius",h.textContent=9*p/5+32+"F"):(x.textContent="Fahrenheit",h.textContent=`${p}°C`)})),c}i.textContent="Invalid Input"}catch(a){i.textContent="Location not found",u.textContent="",s.textContent="",t.textContent="",e.textContent="",n.textContent="",o.textContent="",r.textContent=""}var x,h,p;return{}};document.querySelector(".submit-btn").addEventListener("click",(e=>{e.preventDefault(),t()}))})();